import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
import Container from 'react-bootstrap/esm/Container';
import Button from 'react-bootstrap/esm/Button';
import { useState, useEffect } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import Modal from 'react-bootstrap/Modal';



// Path: espinal/src/pages/Projects.js


function ProjectsGrid () {

    const url = "http://localhost:8000";

    // Consulta a la API los proyectos
    const [proyectos, setProyectos] = useState([]);

    useEffect(() => {
        getProjects();
    }, []);

    async function getProjects() {
        const res = await axios.get(`${url}/projects`);
        setProyectos(res.data);
    }

    // Constantes para manejo de alertas
    const [id, setId] = useState(0);
    const [alert, setAlert] = useState(false);

    // Elimina un proyecto
    async function deleteProject(id) {
        await axios.delete(`${url}/projects/${id}`);
        getProjects();
    }




    return (
        <div>
            <div>
            <Container>
                <Row>
                    <Col><h2>Proyectos</h2></Col>
                    <Col xs lg="2">
                        <Button variant="success" size="lg">Crear proyecto</Button>
                    </Col>
                </Row>
            </Container>
            </div>
            <br/>
            <Row xs={1} md={2} className="g-4">
            { proyectos.map((proyecto) => (
                <Col>
                <Card >
                    <Card.Body>
                    <Card.Title>{proyecto.name}</Card.Title>
                    <br/>
                    <Card.Text>
                        <Container>
                        <Table striped bordered hover>
                        <tbody>
                            <tr>
                            <td>Tipo</td>
                            <td>{proyecto.type}</td>
                            </tr>
                            <tr>
                            <td>Ubicación</td>
                            <td>{proyecto.ubication}</td>
                            </tr>
                            <tr>
                            <td>Estado</td>
                            <td>{proyecto.state}</td>
                            </tr>
                        </tbody>
                        </Table>

                            <Button variant="warning" size="sm">
                                Editar
                            </Button> {' '}

                            <Button variant="danger" size="sm" 
                            onClick={() => {deleteProject(proyecto.id)}}>Eliminar</Button>

                        </Container>
                    </Card.Text>
                    </Card.Body>
                </Card>
                </Col>
            ))}
            </Row>
            <Modal
            show={alert}
            onHide={handleCloseAlert}
            backdrop="static"
            keyboard={false}
          >
            <Modal.Header closeButton>
              <Modal.Title>¿Estas seguro de eliminar la asignación?</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              La asignación será eliminada de forma permanente.
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleDeleteM}>
                Eliminar
              </Button>
              <Button variant="primary" onClick={handleCloseAlert}>
                Cerrar
              </Button>
            </Modal.Footer>
          </Modal>
        </div>



      );
} 

export default ProjectsGrid

