import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
import Container from 'react-bootstrap/esm/Container';
import Button from 'react-bootstrap/esm/Button';
import { useState, useEffect } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';



// Path: espinal/src/pages/Projects.js


function ProjectsGrid () {

    const url = "http://localhost:8000";

    // Consulta a la API los proyectos
    const [proyectos, setProyectos] = useState([]);

    useEffect(() => {
        getProjects();
    }, []);

    async function getProjects() {
        const res = await axios.get(`${url}/projects`);
        setProyectos(res.data);
    }

    console.log(proyectos);



    return (
        <div>
            <h1>Proyectos</h1>
            <Button variant="success" size="sm">Crear</Button>

            <Row xs={1} md={2} className="g-4">
            { proyectos.map((proyecto) => (
                <Col>
                <Card >
                    <Card.Body>
                    <Card.Title>{proyecto.name}</Card.Title>
                    <br/>
                    <Card.Text>
                        <Container>
                        <Table striped bordered hover>
                        <tbody>
                            <tr>
                            <td>Tipo</td>
                            <td>{proyecto.type}</td>
                            </tr>
                            <tr>
                            <td>Ubicaci√≥n</td>
                            <td>{proyecto.ubication}</td>
                            </tr>
                            <tr>
                            <td>Estado</td>
                            <td>{proyecto.state}</td>
                            </tr>
                        </tbody>
                        </Table>

                            <Button variant="warning" size="sm">Editar</Button> {' '}

                            <Button variant="danger" size="sm">Eliminar</Button>

                        </Container>
                    </Card.Text>
                    </Card.Body>
                </Card>
                </Col>
            ))}
            </Row>
        </div>



      );
} 

export default ProjectsGrid

