import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
import Container from 'react-bootstrap/esm/Container';
import Button from 'react-bootstrap/esm/Button';
import { useState, useEffect } from 'react';
import axios from 'axios';


// Path: espinal/src/pages/Projects.js


function Projects () {

    const url = "http://localhost:8000";

    // Consulta a la API los proyectos
    const [proyectos, setProyectos] = useState([]);

    useEffect(() => {
        getProjects();
    }, []);

    async function getProjects() {
        const res = await axios.get(`${url}/projects`);
        setProyectos(res.data);
    }

    console.log(proyectos);



    return (


        <Row xs={1} md={2} className="g-4">
        { proyectos.map((proyecto) => (
            <Col>
            <Card>
                <Card.Body>
                <Card.Title>{proyecto.name}</Card.Title>
                <br/>
                <Card.Text>
                    <Container>
                    <Row>
                        <Col>Tipo</Col>
                        <Col xs={6}>{proyecto.type}</Col>
                        <Col><Button variant="warning" size="sm">Editar</Button></Col>
                    </Row>
                    <Row>
                        <Col>Ubicaci√≥n</Col>
                        <Col xs={6}>{proyecto.ubication}</Col>
                        <Col><Button variant="danger" size="sm">Eliminar</Button></Col>
                    </Row>
                    <Row>
                        <Col>Estado</Col>
                        <Col xs={6}>{proyecto.state}</Col>
                        <Col/>
                    </Row>
                    </Container>
                </Card.Text>
                </Card.Body>
            </Card>
            </Col>
        ))}
        </Row>



      );
} 

export default Projects

