import React, { Component } from "react";
import moment from "moment";
import axios from "axios";
import { useState } from "react";

import Timeline, {
  TimelineHeaders,
  SidebarHeader,
  DateHeader
} from "react-calendar-timeline/lib";



export default class App extends Component {
  constructor(props) {
    super(props);

    const url = 'http://localhost:8000';

    [grupos, setGrupos] = useState([])

    // Consultar a la API
    const personas = async () => {
      const response = await axios.get(`${url}/people`);
      const data = response.data;
      console.log(data);
      return data;
    };

    let grupos_1 = []

    const grupos = personas().then((data) => {
      data.forEach((persona) => {
        grupos_1.push({id: persona.id, title: `${persona.name} ${persona.lastname}`, stackItems: true})
      });
      console.log(grupos_1);
      return data;
    });


    //const { groups, items } = generateFakeData(150);
    const groups = grupos_1;
    const items = [
      {
        id: 1,
        group: 1,
        title: 'item 1',
        start_time: moment(),
        end_time: moment().add(1, 'hour')
      },
      {
        id: 2,
        group: 2,
        title: 'item 2',
        start_time: moment().add(-0.5, 'hour'),
        end_time: moment().add(0.5, 'hour')
      },
      {
        id: 3,
        group: 1,
        title: 'item 3',
        start_time: moment().add(2, 'hour'),
        end_time: moment().add(3, 'hour')
      }
    ];

    const defaultTimeStart = moment()
      .startOf("day")
      .toDate();
    const defaultTimeEnd = moment()
      .startOf("day")
      .add(1, "day")
      .toDate();

    this.state = {
      groups,
      items,
      defaultTimeStart,
      defaultTimeEnd
    };
  }

  render() {
    console.log(moment().add(1, 'hour'));
    const { groups, items, defaultTimeStart, defaultTimeEnd } = this.state;

    return (
      <Timeline
        groups={groups}
        items={items}
        sidebarContent={<div>Above The Left</div>}
        itemsSorted
        itemTouchSendsClick={false}
        stackItems
        itemHeightRatio={0.75}
        showCursorLine
        canMove={false}
        canResize={false}
        defaultTimeStart={defaultTimeStart}
        defaultTimeEnd={defaultTimeEnd}
      >
        <TimelineHeaders className="sticky">
          <SidebarHeader>
            {({ getRootProps }) => {
              return <div {...getRootProps()}>ELEMENTS</div>;
            }}
          </SidebarHeader>
          <DateHeader unit="primaryHeader" />
          <DateHeader />
        </TimelineHeaders>
      </Timeline>
    );
  }
}
