import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import React, { useState } from 'react';

function NavbarEs() {

  const url = 'http://localhost:8000';


  // Cargar personas
  const [personas, setPersonas] = useState([]);

  // Cargar equipos
  const [equipos, setEquipos] = useState([]);

  // Cargar proyectos
  const [proyectos, setProyectos] = useState([]);


  // Form para asignar personas
  const [showP, setShowP] = useState(false);
  const handleCloseP = () => setShowP(false);

  // En el handleShowP se debe hacer la consulta a la API
  async function handleShowP() {

    const pers = await axios.get(`${url}/people`);
    const proy = await axios.get(`${url}/projects`);
    setPersonas(pers.data);
    setProyectos(proy.data);
    console.log(pers.data);
    console.log(proy.data);
    setShowP(true);
  }






  // Form para asignar equipos
  const [showE, setShowE] = useState(false);
  const handleCloseE = () => setShowE(false);
  const handleShowE = () => setShowE(true);



  // Consultas a la API ¿Hacerlo junto con el handleShowP y handleShowE?


  // Ejemplo de como se ve el formulario
  const proyectos_array = ["Proyecto 1", "Proyecto 2", "Proyecto 3"];


  return (
    <div>
      <div>
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="/">El Espinal SpA</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="/calendar">Calendario</Nav.Link>
              <Nav.Link href="#proyectos">Proyectos</Nav.Link>
              <Nav.Link href="#personas">Personas</Nav.Link>
              <Nav.Link href="#equipos">Equipos</Nav.Link>
              <NavDropdown title="Asignar" id="basic-nav-dropdown">
                <NavDropdown.Item onClick={handleShowP}>Asignar persona</NavDropdown.Item>
                <NavDropdown.Item onClick={handleShowE}>Asignar equipo</NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      </div>


      <div>
        <Modal show={showP} onHide={handleCloseP} backdrop="static">
          <Modal.Header closeButton>
            <Modal.Title>Asignar persona</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Persona</Form.Label>
                <Form.Select aria-label="Default select example">
                  <option>Seleccionar persona</option>
                  {perosnas.map((proyecto) => (
                    <option>{proyecto.name}</option>
                  ))}
                </Form.Select>
                <br />
                <Form.Label>Proyecto</Form.Label>
                <Form.Select aria-label="Default select example">
                  <option>Seleccionar proyecto</option>
                  {proyectos_array.map((proyecto) => (
                    <option>{proyecto}</option>
                  ))}
                </Form.Select>
                <br />
                <Form.Label>Fecha inicio</Form.Label>
                  <Form.Control
                  type="date"
                  name="duedate"
                  placeholder="Due date"
                  // value={date}
                  // onChange={(e) => setDate(e.target.value)}
                />
                <br />
                <Form.Label>Fecha termino</Form.Label>
                  <Form.Control
                  type="date"
                  name="duedate"
                  placeholder="Due date"
                  // value={date}
                  // onChange={(e) => setDate(e.target.value)}
                />

              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseP}>
              Close
            </Button>
            <Button variant="primary" onClick={handleCloseP}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>


      <div>
        <Modal show={showE} onHide={handleCloseE} backdrop="static">
          <Modal.Header closeButton>
            <Modal.Title>Asignar equipo</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="name@example.com"
                  autoFocus
                />
              </Form.Group>
              <Form.Group
                className="mb-3"
                controlId="exampleForm.ControlTextarea1"
              >
                <Form.Label>Example textarea</Form.Label>
                <Form.Control as="textarea" rows={3} />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseE}>
              Close
            </Button>
            <Button variant="primary" onClick={handleCloseE}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>




  );
}

export default NavbarEs;
