import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
import Container from 'react-bootstrap/esm/Container';
import Button from 'react-bootstrap/esm/Button';
import { useState, useEffect } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';



// Path: espinal/src/pages/Projects.js


function Projects () {

    const url = "http://localhost:8000";

    // Consulta a la API los proyectos
    const [proyectos, setProyectos] = useState([]);

    useEffect(() => {
        getProjects();
    }, []);

    async function getProjects() {
        const res = await axios.get(`${url}/projects`);
        setProyectos(res.data);
    }

    console.log(proyectos);



    return (


        <Row xs={1} md={2} className="g-4">
        { proyectos.map((proyecto) => (
            <Col>
            <Card>
                <Card.Body>
                <Card.Title>{proyecto.name}</Card.Title>
                <br/>
                <Card.Text>
                    <Container>
                    <Table striped bordered hover>
                    <tbody>
                        <tr>
                        <td>Tipo</td>
                        <td>{proyecto.type}</td>
                        </tr>
                        <tr>
                        <td>2</td>
                        <td>Jacob</td>
                        </tr>
                        <tr>
                        <td>3</td>
                        <td >Larry the Bird</td>
                        <td>@twitter</td>
                        </tr>
                    </tbody>
                    </Table>
                    <Row xs={1} md={3}>
                        <Col>Tipo</Col>
                        <Col>{proyecto.type}</Col>
                        <Col><Button variant="warning" size="sm">Editar</Button><br/></Col>
                    </Row>
                    <Row xs={1} md={3}>
                        <Col>Ubicaci√≥n</Col>
                        <Col >{proyecto.ubication}</Col>
                        <Col>
                        <Button variant="danger" size="sm">Eliminar</Button>

                        </Col>
                    </Row>
                    <Row xs={1} md={3}>
                        <Col>Estado</Col>
                        <Col >{proyecto.state}</Col>
                        <Col/>
                    </Row>
                    </Container>
                </Card.Text>
                </Card.Body>
            </Card>
            </Col>
        ))}
        </Row>



      );
} 

export default Projects

