import React from "react";
import moment from "moment";
import axios from "axios";
import { useState, useEffect } from "react";

import Timeline, {
  TimelineHeaders,
  SidebarHeader,
  DateHeader
} from "react-calendar-timeline/lib";



function MainTimeline(props) {

    const url = 'http://localhost:8000';
    const [personas, setPersonas] = useState([]);

    // Consultar a la API
    const getPersonas = async () => {
      const response = axios.get(`${url}/people`)
      .then(
        response => {
          setPersonas(response.data);
        }

      )
      .catch(
        error => {
          console.log(error);
        }
      );
    };

    const getEquipos = async () => {
      const response = axios.get(`${url}/equipment`)
      .then(
        response => {
          setGrupos(response.data);
        }

      )
      .catch(
        error => {
          console.log(error);
        }
      );
    };

    const asigned_groups = personas.map(persona => {
      return {
        id: persona.id,
        title: persona.name + ' ' + persona.lastname
      }
    });


    useEffect(() => {
      getPersonas();
    }, []);

    console.log(grupos);
    console.log(asigned_groups);



    //const { groups, items } = generateFakeData(150);
    //setGrupos(grupos_1);

    const groups = [{ id: 1, title: 'group 1' }, { id: 2, title: 'group 2' }]

    const items = [
      {
        id: 1,
        group: 1,
        title: 'item 1',
        start_time: moment(),
        end_time: moment().add(1, 'hour')
      },
      {
        id: 2,
        group: 2,
        title: 'item 2',
        start_time: moment().add(-0.5, 'hour'),
        end_time: moment().add(0.5, 'hour')
      },
      {
        id: 3,
        group: 1,
        title: 'item 3',
        start_time: moment().add(2, 'hour'),
        end_time: moment().add(3, 'hour')
      }
    ]

    const defaultTimeStart = moment()
      .startOf("day")
      .toDate();
    const defaultTimeEnd = moment()
      .startOf("day")
      .add(1, "day")
      .toDate();


    return (
      <Timeline
        groups={asigned_groups}
        items={items}
        sidebarContent={<div>Above The Left</div>}
        itemsSorted
        itemTouchSendsClick={false}
        stackItems
        itemHeightRatio={0.75}
        showCursorLine
        canMove={false}
        canResize={false}
        defaultTimeStart={defaultTimeStart}
        defaultTimeEnd={defaultTimeEnd}
      >
        <TimelineHeaders className="sticky">
          <SidebarHeader>

          </SidebarHeader>
          <DateHeader unit="primaryHeader" />
          <DateHeader />
        </TimelineHeaders>
      </Timeline>
    );
  }

export default MainTimeline;
