import React from "react";
import moment from "moment";
import axios from "axios";
import { useState, useEffect } from "react";

import Timeline, {
  TimelineHeaders,
  SidebarHeader,
  DateHeader
} from "react-calendar-timeline/lib";



function MainTimeline() {

    const url = 'http://localhost:8000';
    //const [personas, setPersonas] = useState([]);
    //const [equipos, setEquipos] = useState([]);
    //const [items, setItems] = useState([]);
    const [grupos, setGrupos] = useState([]);

    // Consultar a la API

    async function getData () {
      const personas = await axios.get(`${url}/people`);
      const equipos = await axios.get(`${url}/equipment`);

      // Setear la informacion
      const asigned_persons = personas.data.map(persona => {
        return {
          id: persona.id,
          title: persona.name + ' ' + persona.lastname
        }
      });
      const asigned_equipment = equipos.data.map(equipo => {
        return {
          id: equipo.id + 100,
          title: equipo.brand + ' ' + equipo.model
        }
      });

      // Concatenar los arrays
      const groups_aux = asigned_persons.concat(asigned_equipment);
      console.log(groups_aux);
      setGrupos(groups_aux);
      return groups_aux;
    }

    const items = [
      {
        id: 1,
        group: 1,
        title: 'item 1',
        start_time: moment(),
        end_time: moment().add(1, 'hour')
      },
      {
        id: 2,
        group: 2,
        title: 'item 2',
        start_time: moment().add(-0.5, 'hour'),
        end_time: moment().add(0.5, 'hour')
      },
      {
        id: 3,
        group: 1,
        title: 'item 3',
        start_time: moment().add(2, 'hour'),
        end_time: moment().add(3, 'hour')
      }
    ]


    const groups = getData()
    //setGrupos(groups);
    useEffect(() => {
      getData();
    }, []);

    console.log(grupos);


    //console.log(personas);
    //console.log(equipos);
    //console.log(asigned_groups);



    //const { groups, items } = generateFakeData(150);
    //setGrupos(grupos_1);



    const defaultTimeStart = moment()
      .startOf("day")
      .toDate();
    const defaultTimeEnd = moment()
      .startOf("day")
      .add(1, "day")
      .toDate();


    return (
      <Timeline
        groups={grupos}
        items={items}
        sidebarContent={<div>Above The Left</div>}
        itemsSorted
        itemTouchSendsClick={false}
        stackItems
        itemHeightRatio={0.75}
        showCursorLine
        canMove={false}
        canResize={false}
        defaultTimeStart={defaultTimeStart}
        defaultTimeEnd={defaultTimeEnd}
      >
        <TimelineHeaders className="sticky">
          <SidebarHeader>
          </SidebarHeader>
          <DateHeader unit="primaryHeader" />
          <DateHeader />
        </TimelineHeaders>
      </Timeline>
    );
  }

export default MainTimeline;
